    private void jbsimpanActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jbsimpanActionPerformed
        supplier_class sup=new supplier_class();
        sup.setKode(jtkode.getText());
        sup.setNama(jtnama.getText());
        sup.setAlamat(jtalamat.getText());
        sup.setTelepon(jttelepon.getText());
        sup.setKontak(jtkontak.getText());
        daftarsupplier.add(sup);
        initstatic();

        // TODO add your handling code here:
    }//GEN-LAST:event_jbsimpanActionPerformed

