    private void addstate() {
     jtkode.setEnabled(true);
        jtnama.setEnabled(true);
        jtalamat.setEnabled(true);
        jttelepon.setEnabled(true);
        jtkontak.setEnabled(true);
        jtkode.requestFocus();
        //aktif simpan button
        jbsimpan.setEnabled(true);
        jbtambah.setEnabled(false);    
    }
